import React, { useState, useEffect } from "react";
import {
  StyleSheet,
  View,
  Text,
  TouchableOpacity,
  FlatList,
} from "react-native";
import LinearGradient from "react-native-linear-gradient";
import VehicleCard from "../components/VehicleCard";
import { api } from "../services/api";
import { COLORS } from "../utils/constants";
import { getAuth, signOut } from "firebase/auth";

const ProfileScreen = ({ navigation }) => {
  const [user, setUser] = useState(null);
  const [listings, setListings] = useState([]);

  useEffect(() => {
    const auth = getAuth();
    setUser(auth.currentUser);
    fetchListings();
  }, []);

  const fetchListings = async () => {
    try {
      const response = await api.get("/vehicles/user");
      setListings(response.data);
    } catch (error) {
      console.error("Error fetching listings:", error);
    }
  };

  const handleLogout = async () => {
    const auth = getAuth();
    await signOut(auth);
    navigation.navigate("Login");
  };

  return (
    <LinearGradient
      colors={[COLORS.primary, COLORS.secondary]}
      style={styles.container}
    >
      <View style={styles.header}>
        <Text style={styles.title}>{user?.displayName || "User Profile"}</Text>
        <TouchableOpacity style={styles.logoutButton} onPress={handleLogout}>
          <Text style={styles.logoutButtonText}>Logout</Text>
        </TouchableOpacity>
      </View>
      <Text style={styles.subtitle}>Your Listings</Text>
      <FlatList
        data={listings}
        keyExtractor={(item) => item.id}
        renderItem={({ item }) => (
          <VehicleCard
            vehicle={item}
            onPress={() =>
              navigation.navigate("VehicleDetail", { vehicle: item })
            }
          />
        )}
        contentContainerStyle={styles.list}
      />
    </LinearGradient>
  );
};

const styles = StyleSheet.create({
  container: { flex: 1, paddingTop: 50 },
  header: {
    padding: 16,
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
  title: { fontSize: 24, color: "#fff", fontWeight: "bold" },
  subtitle: { fontSize: 20, color: "#fff", padding: 16 },
  list: { padding: 16 },
  logoutButton: {
    backgroundColor: COLORS.accent,
    padding: 10,
    borderRadius: 8,
  },
  logoutButtonText: { color: "#fff", fontSize: 16 },
});

export default ProfileScreen;
