import React from "react";
import { StyleSheet, View, Text, Image, TouchableOpacity } from "react-native";
import { formatPrice } from "../utils/helpers";
import { COLORS } from "../utils/constants";

const VehicleCard = ({ vehicle, onPress }) => (
  <TouchableOpacity style={styles.card} onPress={onPress}>
    <Image
      source={{
        uri: vehicle.image || require("../assets/images/placeholder.png"),
      }}
      style={styles.image}
    />
    <View style={styles.content}>
      <Text style={styles.title}>{vehicle.title}</Text>
      <Text style={styles.price}>{formatPrice(vehicle.price)}</Text>
      <Text style={styles.location}>{vehicle.location}</Text>
    </View>
  </TouchableOpacity>
);

const styles = StyleSheet.create({
  card: {
    flexDirection: "row",
    backgroundColor: "#fff",
    borderRadius: 8,
    marginBottom: 16,
    shadowColor: "#000",
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.2,
    shadowRadius: 4,
  },
  image: {
    width: 120,
    height: 100,
    borderTopLeftRadius: 8,
    borderBottomLeftRadius: 8,
  },
  content: { flex: 1, padding: 12 },
  title: { fontSize: 18, fontWeight: "bold", color: "#333" },
  price: { fontSize: 16, color: COLORS.accent, marginVertical: 4 },
  location: { fontSize: 14, color: "#666" },
});

export default VehicleCard;
