import React, { useState } from "react";
import {
  StyleSheet,
  View,
  Text,
  Modal,
  TouchableOpacity,
  TextInput,
} from "react-native";
import { COLORS } from "../utils/constants";

const FilterModal = ({ visible, onClose, onApply, currentFilters }) => {
  const [category, setCategory] = useState(currentFilters.category);
  const [minPrice, setMinPrice] = useState(
    currentFilters.priceRange[0].toString()
  );
  const [maxPrice, setMaxPrice] = useState(
    currentFilters.priceRange[1].toString()
  );
  const [location, setLocation] = useState(currentFilters.location);

  const handleApply = () => {
    onApply({
      category,
      priceRange: [parseInt(minPrice) || 0, parseInt(maxPrice) || 1000000],
      location,
    });
  };

  return (
    <Modal visible={visible} transparent animationType="slide">
      <View style={styles.modalContainer}>
        <View style={styles.modalContent}>
          <Text style={styles.modalTitle}>Filter Vehicles</Text>
          <Text style={styles.label}>Category</Text>
          <TextInput
            style={styles.input}
            placeholder="e.g., Car, Bike"
            value={category}
            onChangeText={setCategory}
          />
          <Text style={styles.label}>Price Range</Text>
          <View style={styles.priceRange}>
            <TextInput
              style={[styles.input, styles.priceInput]}
              placeholder="Min"
              value={minPrice}
              onChangeText={setMinPrice}
              keyboardType="numeric"
            />
            <TextInput
              style={[styles.input, styles.priceInput]}
              placeholder="Max"
              value={maxPrice}
              onChangeText={setMaxPrice}
              keyboardType="numeric"
            />
          </View>
          <Text style={styles.label}>Location</Text>
          <TextInput
            style={styles.input}
            placeholder="e.g., Mumbai"
            value={location}
            onChangeText={setLocation}
          />
          <View style={styles.buttonContainer}>
            <TouchableOpacity style={styles.button} onPress={onClose}>
              <Text style={styles.buttonText}>Cancel</Text>
            </TouchableOpacity>
            <TouchableOpacity
              style={[styles.button, styles.applyButton]}
              onPress={handleApply}
            >
              <Text style={styles.buttonText}>Apply</Text>
            </TouchableOpacity>
          </View>
        </View>
      </View>
    </Modal>
  );
};

const styles = StyleSheet.create({
  modalContainer: {
    flex: 1,
    justifyContent: "center",
    backgroundColor: "rgba(0,0,0,0.5)",
  },
  modalContent: {
    backgroundColor: "#fff",
    margin: 20,
    padding: 20,
    borderRadius: 8,
  },
  modalTitle: { fontSize: 20, fontWeight: "bold", marginBottom: 16 },
  label: { fontSize: 16, marginBottom: 8 },
  input: {
    backgroundColor: "#f0f0f0",
    borderRadius: 8,
    padding: 12,
    fontSize: 16,
    marginBottom: 16,
  },
  priceRange: { flexDirection: "row", justifyContent: "space-between" },
  priceInput: { flex: 1, marginRight: 10 },
  buttonContainer: { flexDirection: "row", justifyContent: "flex-end" },
  button: { padding: 12, borderRadius: 8, marginLeft: 10 },
  applyButton: { backgroundColor: COLORS.accent },
  buttonText: { color: "#fff", fontSize: 16 },
});

export default FilterModal;
